import React, { useState } from 'react'
import { Modal, Skeleton, Typography, Descriptions, Button, Image, App } from 'antd'
import type { DescriptionsProps } from 'antd'
import { formatCurrencyVND } from '~/utils/numberUtils'
import { getBirdByIdAPI } from '~/utils/api'
import BirdEdit from './birdEdit'
const { Link } = Typography

type BirdImage = {
  id: number
  imageUrl: string
}

export type BirdDetail = {
  birdType: string
  bird_images: BirdImage[]
  categoryName: string
  competitionAchievements: number
  countRating: number
  description: string
  gender: string
  id: number
  name: string
  price: number
  purebredLevel: string
  quantity: number
  sold: number
  thumbnail: string
  totalRating: number
  color: string
  status: boolean
}

type BirdDetailProps = {
  id: number
}

const BirdDetail: React.FC<BirdDetailProps> = ({ id }) => {
  const { message, notification } = App.useApp()
  const [open, setOpen] = useState(false)
  const [confirmLoading, setConfirmLoading] = useState(false)
  const [loading, setLoading] = useState<boolean>(false)
  const [bird, setBird] = useState<BirdDetail>()
  const items: DescriptionsProps['items'] = [
    {
      label: 'Tên chim',
      children: bird?.name
    },
    {
      label: 'Trạng thái',
      children: bird?.status ? 'Còn bán' : 'Ngừng bán'
    },
    {
      label: 'Giới tính',
      children: bird?.gender
    },
    {
      label: 'Số lượng',
      children: bird?.quantity
    },
    {
      label: 'Giá tiền',
      span: { md: 2, lg: 2, xl: 2, xxl: 2 },
      children: formatCurrencyVND(bird ? bird.price : 0)
    },
    {
      label: 'Độ thuần chủng',
      span: { xl: 2, xxl: 2 },
      children: bird?.purebredLevel
    },
    {
      label: 'Loài chim',
      span: { xs: 1, sm: 1, md: 1, lg: 1, xl: 2, xxl: 2 },
      children: bird?.birdType
    },
    {
      label: 'Danh mục',
      span: { xs: 1, sm: 1, md: 1, lg: 1, xl: 2, xxl: 2 },
      children: bird?.categoryName
    },
    {
      label: 'Mô tả',
      span: { xs: 1, sm: 2, md: 3, lg: 3, xl: 4, xxl: 4 },
      children: bird?.description
    },
    {
      label: 'Hình ảnh',
      span: { xs: 1, sm: 1, md: 2, lg: 2, xl: 2, xxl: 2 },
      children: (
        <div className='w-full flex items-center justify-center'>
          <Image.PreviewGroup
            items={bird?.bird_images.map((b) => `https://api.techx.id.vn/uploads/birds/${b.imageUrl}`)}
          >
            <Image
              width={200}
              src={`https://api.techx.id.vn/uploads/birds/${bird?.bird_images[0]?.imageUrl}`}
              fallback='data:image/png;base64,'
            />
          </Image.PreviewGroup>
        </div>
      )
    },
    {
      label: 'Ảnh nhỏ',
      span: { xs: 1, sm: 1, md: 1, lg: 1, xl: 2, xxl: 2 },
      children: (
        <div className='w-full flex items-center justify-center'>
          <Image
            width={200}
            src={`https://api.techx.id.vn/uploads/birds/${bird?.thumbnail}`}
            fallback='data:image/png;base64,'
          />
        </div>
      )
    }
  ]

  const showModal = async (e: React.MouseEvent) => {
    e.preventDefault()
    setOpen(true)
    setLoading(true)
    try {
      const response = await getBirdByIdAPI(id)
      setLoading(false)
      if (response) {
        setBird(response.data)
      } else {
        notification.error({ message: 'Sorry! Something went wrong. App server error' })
      }
    } catch (err) {
      setLoading(false)
      notification.error({ message: (err as string) || 'Sorry! Something went wrong. App server error' })
    }
  }

  const handleOk = () => {
    setConfirmLoading(true)
    setTimeout(() => {
      setOpen(false)
      setConfirmLoading(false)
      message.success('Click on Yes')
    }, 2000)
  }

  const handleCancel = () => {
    console.log('Clicked cancel button')
    setOpen(false)
  }

  return (
    <>
      <Link href='' onClick={showModal}>
        Xem
      </Link>

      <Modal
        title='Thông tin chim'
        style={{ top: 20 }}
        width={1000}
        open={open}
        onOk={handleOk}
        onCancel={handleCancel}
        maskClosable={false}
        footer={[
          <Button key='back' onClick={handleCancel}>
            Hủy
          </Button>,
          <Button key='submit' type='primary' loading={confirmLoading} disabled={loading} onClick={handleOk}>
            Đồng ý
          </Button>
        ]}
      >
        <Skeleton loading={loading} active paragraph={{ rows: 6 }}>
          <Descriptions
            extra={<BirdEdit bird={bird} setBird={setBird} />}
            bordered
            column={{ xs: 1, sm: 2, md: 3, lg: 3, xl: 4, xxl: 4 }}
            items={items}
          />
        </Skeleton>
      </Modal>
    </>
  )
}

export default BirdDetail
